package bucles_for;

public class for_a_while {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		 
		for(int c=0; c <= 10 ; c++) {
            System.out.println("c="+c);
        }
        
		
		int a=0;
		
		while(a<=10) {
			
			System.out.println("a= "+a);
			a++;
		}
		
		int b=0;
		do {
			System.out.println("b="+b);
			b++;
		}while(b<=10);
	}

}
