package paquete1;

public class clase2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double x=3.14;
		double y=-10;
		
		//abs
	
		double z=Math.abs(y);
		
		System.out.println(z);
		
		//sqrt
		
		 z=Math.sqrt(y);
		 
		 System.out.println(z);
		
		//round
		
		z=Math.round(x);
		 
		System.out.println(z);
		
		//ceil
		
		z=Math.ceil(x);
		
		System.out.println(z);
		
		//floor
		
		z=Math.floor(x);
		
		System.out.println(z);
		
	}

}
